<?php/*Forms can be defined in the code but it more flexible to play with them outside.Our forms are defined in the XML file and are created and returned, when needed, bythe class below available to every controller.*/class Zend_Controller_Action_Helper_FormsFactory extends Zend_Controller_Action_Helper_Abstract {  public function direct($formName) {    // accessing application configuration    $config = Config::singleton();        // loading the XML form definitions    $definitions = new Zend_Config_Xml($config->settings->forms->path, $config->settings->forms->container);    // dynamically creating a new form    $form = new Zend_Form($definitions->$formName);        return $form;  }}?>